import './ExpenseItem.css'

function ExpenseItem(props) {
  const day = props.date.toLocaleString('en-US', { day: 'numeric' })
  const month = props.date.toLocaleString('en-US', { month: 'long' })
  const year = props.date.toLocaleString('en-US', { year: 'numeric' })

  const expnTitle = props.title
  const expAmount = props.amount

  return (
    <div className="expense-item">
      <div>
        <div>{props.date.toLocaleString('en-US', { month: 'long' })}</div>
        <div>{props.date.toLocaleString('en-US', { year: 'numeric' })}</div>
        <div>{props.date.toLocaleString('en-US', { day: 'numeric' })}</div>
      </div>
      <div className="expense-item__description">
        <h2>{expnTitle}</h2>
        <div className="expense-item__price">{expAmount}</div>
      </div>
    </div>
  )
}

export default ExpenseItem
